package com.knifed.bookstore.repositories;

import com.knifed.bookstore.entities.Book;
import org.springframework.stereotype.Repository;

import java.util.ArrayList;
import java.util.List;

@Repository
public class BookRepository {
    private List<Book> booksList = new ArrayList<Book>();

    public BookRepository(){
        Book book1 = new Book("50 Shades of Gray", "Dorian Gray", 70, "data:image/jpeg;base64,", "nice description", 3);
        Book book2 = new Book("Amazing Adventures of Spider-Man", "Stan Lee", 60, "data:image/jpeg;base64,", "even better description", 6);
        Book book3 = new Book("The Five People You Meet in Heaven", "Mitch Albom", 35, "https://images-na.ssl-images-amazon.com/images/I/41CdFqzQ84L._SX351_BO1,204,203,200_.jpg", "nice description", 3);
        Book book4 = new Book("The Fault In Our Stars", "John Green", 70, "data:image/jpeg;base64,", "nice description", 3);
        Book book5 = new Book("Before I Fall", "Lauren Oliver", 35, "https://images-na.ssl-images-amazon.com/images/I/514aWlRMJKS._SX330_BO1,204,203,200_.jpg", "nice description", 3);

        this.booksList.add(book1);
        this.booksList.add(book2);
        this.booksList.add(book3);
        this.booksList.add(book4);
        this.booksList.add(book5);
    }

    public List<Book> getBooksList(){
        return this.booksList;
    }

    public void addBook(Book book){
        this.getBooksList().add(book);
    }

    public void deleteBook(Book book){
        Book bookToBeDeleted = this.getBooksList().stream()
                                        .filter(b -> b.getName().equals(book.getName()))
                                        .findFirst()
                                        .orElse(null);
        this.booksList.remove(bookToBeDeleted);
    }

    public void updateBook(Book book){
        Book bookToBeUpdated = this.getBooksList()
                                    .stream()
                                    .filter(b -> b.getName().equals(book.getName()))
                                    .findFirst()
                                    .orElse(null);
        bookToBeUpdated.setQuantity(book.getQuantity());
    }
}
